    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        
        String c1 = Num1.getText();
        Double Dato1 = Double.parseDouble(c1);
        
        String c3 = opcion.getText();
        int Opcion = Integer.parseInt(c3);
        
        do{
            switch (Opcion){
                case 1:
                    double kilogramos = Dato1 * 0.45359237;
                    Resultado.setText(Dato1 + " Libras equivalen a " + kilogramos + " Kilogramos.");
                    break;
                case 2:
                    double kilometros = Dato1 * 1.60934;
                    Resultado.setText(Dato1 + " Millas equivalen a " + kilometros + " Kilómetros.");
                    break;
                case 3:
                   double litros = Dato1 * 3.78541;
                    System.out.println(Dato1 + " Galones equivalen a " + litros + " Litros.");
                    break;
                case 4:
                        double celsius = (Dato1 - 32) * 5/9;
                    System.out.println(Dato1 + " grados Fahrenheit equivalen a " + celsius + " grados Celsius.");
                    break;
                case 5:
                    this.dispose(); //si encuentran como cerrarlo me hacen el fa ;)
                    break;
                default:
                    System.out.println("Opción no válida. Por favor, elige una opción del menú.");
            }
                    
            
        } while (Opcion > 5);
        
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

