    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
        
        int n=0;
        n = Integer.parseInt(Campo1.getText());
        
        while (n < 1 || n > 10) {
            n = Integer.parseInt(JOptionPane.showInputDialog(null, "Digite un n√∫mero natural entre 1 y 10 para visualizar su tabla"));
        }

        StringBuilder tabla = new StringBuilder();
        for (int i = 1; i <= 10; i++) {
            int resultado = n * i;
            tabla.append(n).append(" x ").append(i).append(" = ").append(resultado).append("\n");
        }
        r.setText(tabla.toString());
    }//GEN-LAST:event_CalcularActionPerformed

