    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
      int it = Integer.parseInt(Campo1.getText());
        for(int i=1; i<=it; i++){
		
			child += Integer.parseInt(JOptionPane.showInputDialog(null,"¿Cuantos boletos de niños se vendieron en el dia N° "+i+"?"));
			grownass += Integer.parseInt(JOptionPane.showInputDialog(null,"¿Cuantos boletos de adulto se vendieron en el dia N°"+i+"?"));
			off += Integer.parseInt(JOptionPane.showInputDialog(null,"¿Cuantas boletos en promocion se vendieron en el dia N°"+i+"?"));
			
		}
        
        r.setText("Boletos vendidos: " +(child+grownass+off));
        r1.setText("Venta bruta: " +(child*30+grownass*60+off*20));
        r2.setText("Boletos Niños: " +child*30);
        r3.setText("Boletos adultos: " +grownass*60);
        r4.setText("Boletos promocion: " +off*20);
    }//GEN-LAST:event_CalcularActionPerformed

    private void ReinciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ReinciarActionPerformed
       
         child = 0;
         grownass = 0;
         off = 0;
         Campo1.setText("");
         r.setText("");
        r1.setText("");
        r2.setText("");
        r3.setText("");
        r4.setText("");
    }//GEN-LAST:event_ReinciarActionPerformed

