    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
      int suma= 0;
int num = Integer.parseInt(Campo1.getText());
      
      if(num<0){
        r.setText("Ingrese un numero positivo");
        }else{
            if(num==0){
           r.setText("El promedio de los numeros inresados es: "+promedio);
            }else{
                 suma +=num;
                 promedio = suma/num;
            }
        
    }      

    }//GEN-LAST:event_CalcularActionPerformed

