    private void OrdenarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OrdenarActionPerformed
     
         String entrada = Campo1.getText();
        String vocalesOrdenadas = ordenarVocales(entrada);
        r.setText(vocalesOrdenadas);
    }

    public String ordenarVocales(String entrada) {
        if (entrada.length() != 5) {
            return "Debe ingresar exactamente cinco vocales.";
        }

        char[] vocales = entrada.toCharArray();

        for (int i = 0; i < vocales.length - 1; i++) {
            for (int j = i + 1; j < vocales.length; j++) {
                if (vocales[i] > vocales[j]) {
                    char temp = vocales[i];
                    vocales[i] = vocales[j];
                    vocales[j] = temp;
                }
            }
        }

        return new String(vocales);
    

    }//GEN-LAST:event_OrdenarActionPerformed

