    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
      int n=0;
	          
        while(n<=0 || n>100){n = Integer.parseInt(Campo1.getText());}
    	
    	if (n>=1 && n<=12){

    	    r.setText("Esta¡ en la niñez.");
			
    	}else if (n>=13 && n<=18){

    	   r.setText("Esta¡ en la adolescencia.");
    	    
    	}else if (n>=19 && n<=30){
 
			r.setText("Esta¡ en la juventud.");
			
    	}else if (n>=31 && n<=60){
 
    	    r.setText("Esta¡ en la adultez.");
			
    	}else if (n>=61 && n<=100){
 
    	    r.setText("Esta¡ en la vejez.");
			
    	}
		
	
    }//GEN-LAST:event_CalcularActionPerformed

