    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
        int denominador = Integer.parseInt(Campo1.getText());
            double numerador1 = Double.parseDouble(Campo2.getText());
            double numerador2 = Double.parseDouble(Campo3.getText());

            double fraccion1 = numerador1 / denominador;
            double fraccion2 = numerador2 / denominador;
            double suma = fraccion1 + fraccion2;
            double resta = fraccion1 - fraccion2;

            String resultado = numerador1 + "/" + denominador + " + " + numerador2 + "/" + denominador + " = " + suma + "\n\n" +
                              numerador1 + "/" + denominador + " - " + numerador2 + "/" + denominador + " = " + resta;
            r.setText(resultado);
    
    }//GEN-LAST:event_CalcularActionPerformed

